import { useState } from 'react';
import { Link, useHistory } from 'react-router-dom'
import { auth } from '../config/firebaseConfig'

const Navbar = () => {
  const [isAuth, setIsAuth] = useState(false)
  const history  = useHistory()
  try {
    auth.onAuthStateChanged((user) => {
      if (user) {
        setIsAuth(true)
      } else {
        setIsAuth(false)
      }
  });

  } catch (error) {

  }
  const logout = (e) => {
    e.preventDefault()
    auth.signOut()
    history.push('/login')
  }

  return (
    <nav className="navbar">
      <h1>The Dojo Blog</h1>
      <div className="links">
        <Link to="/">Home</Link>
        {isAuth && <Link to="/create" style={{ 
          color: 'white', 
          backgroundColor: '#f1356d',
          borderRadius: '8px' 
        }}>New Blog
        </Link>}
        {!isAuth ?
          <Link to="/login">
            Login
          </Link>

          : <form className="logout" onSubmit={logout}>
              <button type="sbumit">Logout</button>
            </form>
        }
      </div>
    </nav>
  );
}

export default Navbar;
